#
# Procedure "insert_user"
#

DROP PROCEDURE IF EXISTS `insert_user`;
CREATE PROCEDURE `insert_user`(_username varchar(128),_nickname varchar(128),_pwd varchar(128),_mail varchar(128),_type INT,_code varchar(128))
BEGIN 
	INSERT INTO user (`username`, `nickname`, `pwd`, `mail`)VALUES (_username, _nickname, _pwd, _mail);
	select uid INTO @u_id from user where `username`=_username and `mail`=_mail LIMIT 0,1;
	INSERT INTO user_code (`uid`, `type`, `code`) VALUES (@u_id, _type, _code);
	select @u_id;
END;
